// "Live audio in looper" @by ilesinge

// Usage:
// Record each cycle from the default audio interface
// s("looper")
// Output the last recorded cycle
// s("loop")
// Give the loop another sample name
// s("looper ~ cheese").loopName("cheese")
// Give the loop a specific sample number
// s("looper:0 looper:1")

// It could be used to sample other software's output
// by plugging in a virtual audio interface (see TODO below)

// Note: a rest is needed between the looper and the loop
// otherwise the sample is not yet recorded or overwritten
// so it takes the previous cycle's recording

await navigator.mediaDevices.getUserMedia({ audio: true })
  .then(function (stream) {
    window.stream = stream;
  })
  .catch(function (error) {
    console.log(error);
  });

let loopers = {};

createParam('loopName');
Pattern.prototype['lname'] = Pattern.prototype['loopName'];

registerSound(
  'looper',
  (time, value, onended) => {
    const ctx = getAudioContext();
    let n = value.n || 0;
    let loopName = value.loopName || 'loop';

    let chunks = [];

    loopers[loopName] = loopers[loopName] || [];

    /* MediaRecorder may be passed a second 'option' parameters, such as:
    const options = {
      audioBitsPerSecond: 128000,
      audioBitrateMode: 'constant',
      mimeType: "audio/wav",
    };*/
    let recorder = new MediaRecorder(window.stream);
    recorder.ondataavailable = function (e) {
      chunks.push(e.data);
    };
    recorder.onstop = function (e) {
      const blob = new Blob(chunks);
      chunks = [];
      const audioURL = window.URL.createObjectURL(blob);
      loopers[loopName][n] = audioURL;
      // accessing intermediate samples (undefined) will result in the following error:
      // [cyclist] error: Cannot read properties of undefined (reading 'replace')
      registerSound(loopName, (t, hapValue, onended) => onTriggerSample(t, hapValue, onended, loopers[loopName]), {
        type: 'sample',
        samples: loopers[loopName],
      })
    }

    setTimeout(
      () => {
        recorder.start();
      },
      Math.max(0, (time - ctx.currentTime) * 1000),
    );

    const stop = function (endtime) {
      setTimeout(
        () => {
          recorder.stop();
        },
        Math.max(0, (endtime - ctx.currentTime) * 1000),
      );
    };
    return { undefined, stop };
  }
);

